import { useAuthContext } from "./useAuthContext";
export default function useLogout(){
    const {dispatch} = useAuthContext();
    function logout(){
        localStorage.removeItem('user')

        dispatch({
            type: 'LOGOUT'
        })
    }
    return {logout};
}
