import React from 'react';
import { BrowserRouter as Router, Route, Redirect, Switch } from "react-router-dom";
import { useAuthContext } from './hooks/useAuthContext';
import Navbar from './components/navbar';
import "bootstrap/dist/css/bootstrap.css"
import Edit_staff from  "./components/edit_staff"
import Add_staff from './components/add_staff';
import View_staff from './components/view_staff';
import Footer from './components/footer';
import Login from './components/login';
import {Helmet} from 'react-helmet'
import Signup from './components/signup';
import RedirectLogin from './components/RedirectLogin';
function App() {
  const { user } = useAuthContext();

  return (
    <div className='Main'>

      {/* <Helmet>
        <title>Manage Your Employees</title>
      </Helmet> */}
    <Router>
      <Navbar />
      <Switch>
        <Route path="/"  exact component= {!user ? {Login}: {RedirectLogin}}  />
        <Route path="/signup"  exact component={Signup} />
        <Route path="/view"  component={View_staff} />
        <Route path="/update/:id"  component={Edit_staff} />
        <Route path="/create"  component={Add_staff} />
      </Switch>
    </Router>
    <Footer />
    </div>
  );
}

export default App;
