import React, { useState } from 'react'
import "../css/add.css"
import {Helmet} from "react-helmet"
import axios from "axios"
function Add_staff(props) {
  const [state, setState] = useState(
    {
      firstname: "",
      lastname: "",
      age: null,
      salary: null,
      
    });
    const id = props.match.params.id;
    const FirstnameInputChange = (e) => {
        setState({...state, firstname: e.target.value})
    }
    const LastnameInputChange = (e) => {
        setState({...state, lastname: e.target.value})
    }
    const AgeInputChange = (e) => {
        setState({...state, age: e.target.value})
    }
    const SalaryInputChange = (e) => {
        setState({...state, salary: e.target.value})
    }
    const HandleSubmit = (e) =>{
      e.preventDefault();

      const staff = {
        firstname :state.firstname,
        lastname :state.lastname,
        age :state.age,
        salary :state.salary,
    }
    console.log(staff)
        axios.post('http://localhost:5000/staff/create', staff)
        .then(res => console.log(res.data))
        window.location = '/create'
    //   setState({
    //     firstname: "",
    //     lastname: "",
    //     age: 0,
    //     salary: 0
    // })

    }
  return (
    <section className='Add' >
      <Helmet>
        <title>Add an  Employee</title>
      </Helmet>
        <h2 className='text-center'>Add a new Employee</h2>
    <form  onSubmit={HandleSubmit}>
        <div className="mb-3">
    <label for="firstname" className="form-label">First Name</label>
    <input type="text" value={state.firstname} className="form-control" id="firtname" onChange={FirstnameInputChange} required/>
  </div>
  <div className="mb-3">
    <label for="lastname" className="form-label">Last Name</label>
    <input type="text" value={state.lastname} className="form-control" id="lastname" onChange={LastnameInputChange} required/>
  </div>
  <div className="mb-3">
    <label for="age" className="form-label">Age</label>
    <input type="number" value={state.age} className="form-control" id="age" onChange={AgeInputChange} required/>
  </div>
  <div className="mb-3">
    <label for="salary" className="form-label">Salary</label>
    <input type="number" value={state.salary} className="form-control" id="salary" onChange={SalaryInputChange} required/>
  </div>
  <div className='text-center'>
  <button type="submit" className="btn btn-primary btn-lg w-50 ">Submit</button>
  </div>
    </form>
    </section>
  )
}

export default Add_staff
