import React from 'react';
import {BrowserRouter as Router, Route} from "react-router-dom";
import Navbar from './components/navbar';
import "bootstrap/dist/css/bootstrap.css"
import Edit_staff from  "./components/edit_staff"
import Add_staff from './components/add_staff';
import View_staff from './components/view_staff';
import Footer from './components/footer';
import {Helmet} from 'react-helmet'
function App() {

  return (
    <div className='Main'>
      <Helmet>
        <title>Manage Your Employees</title>
      </Helmet>
    <Router>
      <Navbar />
      <Route path="/"  exact component={View_staff} />
      <Route path="/view"  component={View_staff} />
      <Route path="/update/:id"  component={Edit_staff} />
      <Route path="/create"  component={Add_staff} />
    </Router>
    <Footer />
    </div>
  );
}

export default App;
