import React, { Component } from 'react'
import {Link} from "react-router-dom";
import axios from 'axios';

const Employee= props =>
 <tr>
   <td>{props.employee.firstname}</td>
    <td>{props.employee.lastname}</td>
    <td>{props.employee.age}</td>
   <td>{props.employee.salary}</td>
    <td>
      {/* empty for now */}
   </td>
 </tr>

export class View_staff extends Component {
  constructor(props){
    super(props);
    this.state = {staff:[]}
  }
  componentDidMount(){
    axios.get('http://localhost:5000/staff/employees/')
    .then(res=> {
      this.setState({staff: res.data})
    })
    .catch(err => {
      console.log('error')
    })
  }
  staffList(){
    return this.state.staff.map(currentemployee=>{
      return <Employee employee={currentemployee} key={currentemployee._id} />
    })
  }

  render() {
    return (
      <div>
      <h3>Available Employees</h3>
    <table className="table">
    <thead className="thead-light">
    <tr>
      <th>firstname</th>
      <th>lastname</th>
      <th>Age</th>
      <th>Salary</th>
      <th>Actions</th>
    </tr>
  </thead>
  <tbody>
    {this.staffList()}
  </tbody>
  </table>
</div>
    )
  }
}

export default View_staff
