import React, {useState, useEffect, } from 'react'
import "../css/edit.css"
import {Helmet} from "react-helmet"
import Axios from "axios"

 export default function Edit_staff(props) {
  
const [state, setState] = useState({
      firstname: "",
      lastname: "",
      age: null,
      salary: null,
      staff_firstname: [],
      staff_lastname: []
})

useEffect(() =>{
    const id = props.match.params.id;
    Axios.get('http:// localhost:5000/staff/employees/' + id )
  .then(response=>{
    setState({
      firstname:response.data.firstname,
      lastname:response.data.lastname,
      age:response.data.age,
      salary:response.data.salary,
      
    })
  })
  .catch(function(error){
    console.log(error);
 })
}, [])
const FirstnameInputChange = (e) => {
  setState({...state, firstname: e.target.value})
}
const LastnameInputChange = (e) => {
  setState({...state, lastname: e.target.value})
}
const AgeInputChange = (e) => {
  setState({...state, age: e.target.value})
}
const SalaryInputChange = (e) => {
  setState({...state, salary: e.target.value})
}
const HandleSubmit = (e) =>{
  e.preventDefault();

  const staff = {
      firstname :state.firstname,
      lastname :state.lastname,
      age :state.age,
      salary :state.salary,
      staff_firstname: [],
      staff_lastname: []
    }
        console.log(staff)
        Axios.post('http://localhost:5000/exercises/update/' + props.match.params.id, staff)
        .then(res => console.log(res.data))
        window.location = '/update'
// e.preventDefault();





}

  return (
<section className='Edit' >
    <Helmet>
            <title>Edit an Employee</title>
    </Helmet>
    <h2 className='text-center'>Edit an existing Employee</h2>
<form action="" onSubmit={HandleSubmit}>
<div className="mb-3 mt-4 text-center">
<h6>
    <label for="choose" className="form-label selection-font">Select an Employee</label> 
</h6>  
<select className="form-select"   value={state.firstname } onChange={FirstnameInputChange}>
{
  state.staff_firstname.map(function(staff) {
  return <option key={staff} value={staff} >{staff}</option>
  })
}
</select>
</div>
<div className="mb-3">
<h6 className=' text-center selection-font'>Update Information</h6>
<label for="firstname" className="form-label">First Name</label>
<input type="text" className="form-control" id="firtname" value={state.firstname} onChange={FirstnameInputChange} required/>
</div>
<div className="mb-3">
<label for="lastname" className="form-label">Last Name</label>
<input type="text" className="form-control" id="lastname" value={state.lastname} onChange={LastnameInputChange} required/>
</div>
<div className="mb-3">
<label for="age" className="form-label">Age</label>
<input type="number" className="form-control" id="age" value={state.age} onChange={AgeInputChange} required/>
</div>
<div className="mb-3">
<label for="salary" className="form-label">Salary</label>
<input type="number" className="form-control" id="salary" value={state.salary} onChange={SalaryInputChange} required/>
</div>
<div className='text-center'>
<button type="submit" className="btn btn-primary btn-lg w-50 ">Submit</button>
</div>
</form>
</section>
  )
}
