import React, {Component} from 'react'
import "../css/delete.css"
import {Helmet} from "react-helmet"

const Employee = props => {
  <option>{props.employee.firstname}</option>
}

 export class Delete_staff extends Component {
  constructor(props){
    super(props);
    this.deleteEmployee = this.deleteEmployee.bind(this);
    this.state = {staff:[]}
  }
  componentDidMount(){
    axios.get('http://localhost:5000/staff/employees/')
    .then(res=> {
      this.setState({staff: res.data})
    })
    .catch(err => {
      console.log('error')
    })
  }
  deleteEmployee(id){
    axios.delete('http://localhost:5000/staff/employees/' + id)
    .then(res => console.log(res.data));

    this.setState({
      staff: this.state.staff.filter(staff => staff._id !== id)
    })
  }
  staffList(){
    return this.state.staff.map(currentemployee=>{
      return <Employee employee={currentemployee} key={currentemployee._id} />
    })
  }
  render(){
  return (
    <section className='Delete'>
        <Helmet>
        <title>Delete an  Employee</title>
         </Helmet>
        <h2 className='text-center'>Delete an existing Employee</h2>
    <form action="">
    <div className="mb-3 mt-4 text-center">
        <h6>
            <label for="choose" className="form-label selection-font">Select an Employee</label> 
        </h6>  
        <select className="form-select" id='choose' >
            <option selected>Ex: Abebe Belay</option>
            <option value="1">One</option>
            <option value="2">Two</option>
            <option value="3">Three</option>
        </select>
    </div>
    <div className='mt-5 text-center'>
        <button type="submit" className="btn btn-danger btn-lg w-50 " onClick={() =>{props.deleteEmployee(props.e._id) }}>Delete</button>
    </div>
  </form>
    </section>
  )
  }
}

export default Delete_staff
