import React from 'react';
import { BrowserRouter as Router, Route, Redirect} from "react-router-dom";
import { useAuthContext } from './hooks/useAuthContext';
import Navbar from './components/navbar';
import "bootstrap/dist/css/bootstrap.css"
import Edit_staff from  "./components/edit_staff"
import Add_staff from './components/add_staff';
import View_staff from './components/view_staff';
import Footer from './components/footer';
import Login from './components/login';
import {Helmet} from 'react-helmet'
import Signup from './components/signup';
function App() {
  const user = useAuthContext();

  return (
    <div className='Main'>

      <Helmet>
        <title>Manage Your Employees</title>
      </Helmet>
    <Router>
      <Navbar />
      <Route path="/"  exact component={!user ?{Login}: <Redirect to= "/view"/>} />
      <Route path="/signup"  exact component={!user ?{Signup}: <Redirect to= "/view"/>} />
      <Route path="/view"  component={user ?{View_staff}: <Redirect to= "/"/>} />
      <Route path="/update/:id"  component={user ?{Edit_staff}: <Redirect to= "/"/>} />
      <Route path="/create"  component={user ?{Add_staff}: <Redirect to= "/"/>} />
    </Router>
    <Footer />
    </div>
  );
}

export default App;
